import React from "react";
import "./Joke.css";

// import { render } from "@testing-library/react";
//<JokeCLS text={j.joke} key={j.id} id={j.id} votes={j.votes} vote={this.vote} />
class JokeCLS extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      votes: props.votes,
      numJokesToGet: 10
    }

    this.upVote = this.upVote.bind(this);
    this.downVote = this.downVote.bind(this);
    // this.props.vote = this.props.vote.bind(this);
  }
  upVote = () => {
    // this.setState((state, props) => {
    //   votes:
    // })
    console.log("upVote")
    this.props.vote(this.props.id, +1);
  }
  downVote = () => this.props.vote(this.props.id, -1);

  render() {
    return (
      <div className="Joke">
        <div className="Joke-votearea">
          <button onClick={this.upVote}>
            <i className="fas fa-thumbs-up" />
          </button>

          <button onClick={this.downVote}>
            <i className="fas fa-thumbs-down" />
          </button>

          {this.props.votes}
        </div>

        <div className="Joke-text">{this.props.text}</div>
      </div>
    );
  }
}
export default JokeCLS;
