// import React, { useState, useEffect } from "react";
import React from 'react';
import axios from "axios";
import JokeCLS from "./JokeCLS";
import "./JokeList.css";

class JokeListCLS extends React.Component {
// ({ numJokesToGet = 10 }) {

  // const [jokes, setJokes] = useState([]);
  constructor(props) {
    super(props);
    this.state = {
      jokes: [],
      numJokesToGet: 10
    }

    // this.vote = this.vote.bind(this);
    // this.vote = this.vote.bind(this);
    // this.vote = this.vote.bind(this);
    this.getJokes = this.getJokes.bind(this);
    this.generateNewJokes = this.generateNewJokes.bind(this);
    this.vote = this.vote.bind(this);
  }
  componentDidMount() {


    if (this.state.jokes.length === 0) this.getJokes();
  }
  async getJokes() {
    console.log("this.state.jokes",)
    let j = [...this.state.jokes];
    let seenJokes = new Set();
    try {
      while (j.length < this.state.numJokesToGet) {
        let res = await axios.get("https://icanhazdadjoke.com", {
          headers: { Accept: "application/json" }
        });
        let { status, ...jokeObj } = res.data;

        if (!seenJokes.has(jokeObj.id)) {
          seenJokes.add(jokeObj.id);
          j.push({ ...jokeObj, votes: 0 });
        } else {
          console.error("duplicate found!");
        }
      }
      this.setState((state, props) => ({
        jokes: [...this.state.jokes, j]
      }));
      // this.setState({jokes: [...this.state.jokes, j]});
    } catch (e) {
      console.log(e);
    }
  }
  /* empty joke list and then call getJokes */

  generateNewJokes() {
    this.setState((state, props) => ({
      jokes: []
    }));
    this.getJokes();
    // this.setState({jokes: []});
    // this.setJokes([]);
  }

  /* change vote for this id by delta (+1 or -1) */

  vote(id, delta) {
    this.setState((state, props) => ({
      jokes: state.jokes.map(j => j.id === id ? { ...j, votes: j.votes + delta } : j)
    }));
    // this.setState({jokes: this.jokes.map(j => j.id === id ? { ...j, votes: j.votes + delta } : j)});
  }

  /* render: either loading spinner or list of sorted jokes. */
  render() {
    if (this.state.jokes.length) {
      let sortedJokes = [...this.state.jokes].sort((a, b) => b.votes - a.votes);

      return (
        <div className="JokeList">
          <button className="JokeList-getmore" onClick={this.generateNewJokes}>
            Get New Jokes
          </button>

          {sortedJokes.map(j => (
            <JokeCLS text={j.joke} key={j.id} id={j.id} votes={j.votes} vote={this.vote} />
          ))}
        </div>
      );
    }
    return null;
  }


  // return null;

}

export default JokeListCLS;
